                hasSubs
                ?
                    <ul className={`c-menu__submenu u-list`}>
                        {
                            subs.map((sub, idx) =>
                            {
                                return (
                                    <li key={idx}>{sub.title}</li>
                                )
                            })
                        }
                    </ul>
                : null
            }
        </li>
    )
}
