import React, { useEffect, useState } from 'react'
import { toast } from 'react-toastify'

import axios from 'utils/axios'

export default function ConfigText({ configName, title="" }) {

    const [ value, setValue ] = useState("")
    const [ isEditing, setEditing ] = useState()

    const handleChange = async (e) =>
    {
        const config =
        {
            headers: { 'content-type': 'multipart/form-data' }
        }

        await axios.delete(`/api/config/image/`, { data: { image: value } }, config).catch(err => err)

        const changedFiles = e.target.files
        const formData = new FormData()
        Array.from(changedFiles).map((file, idx) =>
        {
            formData.append('image', file)
        })

        const { success, data, error, info } = await axios.post(`/api/config/image/`, formData, config).catch(err => err)
        if (success)
        {
            setValue(data)
        }
        else {
            toast.error(error)
        }
    }

    const getConfig = async () =>
    {
        const { success, data, error } = await axios.get(`/api/config/?names=${configName}`).catch(err => err)
        if (success)
        {
            setValue(data?.[0]?.value || "")
        }
    }

    useEffect(
        () =>
        {
            getConfig()
        },
        []
    )

    const onSubmit = async () =>
    {
        const body = {
            configs: {
                name: configName,
                value: value
            }
        }
        const { success, data, error, info } = await axios.put(`/api/config/`, body).catch(err => err)
        if (success)
        {
            toast.success(`${title} заслаа`)
        }
        else {
            toast.error(`${title}: ${error}`)
        }
    }

    const handleClick = () =>
    {
        const changed = !isEditing
        if (!changed)
        {
            onSubmit()
        }
        setEditing(changed)
    }

    return (
        <div>
            <label htmlFor={configName}>{title}:</label>
            <input type="file" multiple={false} id={configName} disabled={!isEditing} readOnly={!isEditing} onChange={handleChange} />
            <button onClick={handleClick}>{isEditing ? "save" : "edit"}</button>
            <img src={process.env.REACT_APP_SERVER_URL + value} alt='' width={100}/>
        </div>
    )
}
